import { useState } from 'react';
import { Modal , Button, Form} from 'react-bootstrap';
import axios from "axios"
import {Container , Row, Col,FloatingLabel} from 'react-bootstrap'  
import './Login.css';




const Login = () => {

    const [signUp, setSignUp] = useState(false)


    return(


        <Container className='signinContainer' style={{backgroundColor:'#FFD440', margin: '0 auto', marginTop : '15vh',padding:'30px',width:'30vw'}}>

            <Row style={{display:'inline-flex'}}>
                <Col>
                <Button variant="outline-secondary">Login</Button>
                </Col>
                <Col>
                <Button variant="outline-secondary">Signup</Button>
                </Col>
            </Row>

            <Row>
                {signUp ? <h1 style={{textAlign:'center'}}>Signup</h1> : <h1 style={{textAlign:'center'}}>Login</h1> }
            </Row>
{/*        
                                <Row>
                                    <FloatingLabel controlId="floatingInput" label="Email address" className="mb-3" style={{padding:"5px"}}>
                                        <Form.Control type="email" placeholder="name@example.com" size="sm"/>
                                    </FloatingLabel>

                                </Row>

                                <Row>
                                    <FloatingLabel controlId="floatingPassword" label="Password" style={{padding:"5px"}}>
                                        <Form.Control type="password" placeholder="Password" size='sm'/>
                                    </FloatingLabel>
                                </Row> */}


                {() => {
                        if (signUp) {
                        return (
                                                            <Row>
                                                    <FloatingLabel controlId="floatingInput" label="Email address" className="mb-3" style={{padding:"5px"}}>
                                                        <Form.Control type="email" placeholder="name@example.com" size="sm"/>
                                                    </FloatingLabel>

                                                </Row>
                        )
                        } 
                         else {
                        return (
                            <div>catch all</div>
                        )
                        }
                    }}
            
            <Row md={5} className="justify-content-md-center pt-3">

                {signUp ? <Button variant="primary" size="sm">Signup</Button> : 
                
                <Button variant="primary" size="sm">Login</Button> }

            </Row>
        </Container>
    );

}



export default Login;
