import { Col , Row , Container,Form } from 'react-bootstrap';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Table from 'react-bootstrap/Table';
import { Link } from "react-router-dom";
import axios from "axios"
import './Navigation.css';
import { useEffect, useState } from 'react';





const ManageTeachers = () => {

    const [teachers, setTeachers] = useState([]);

    useEffect( async () =>{
        
        try {
            await axios.get("https://localhost:44323/api/GetTeachers",)
            .then((responce) => {
  
                setTeachers(responce.data)
                console.log(teachers);
        })
        } 
        catch (error) {
            console.error(error)
        }
    },[])

    return(
        <Container style={{marginTop:'50px', maxWidth:'1000px',paddingLeft:'3vw',paddingRight:'3vw'}}>

            <Row>
                <h1>Manage Teachers</h1>
            </Row>

            <Row style={{marginTop:'50px'}}>

            <Table striped bordered hover variant="dark">
                        <thead>
                            <tr>
                            <th>ID</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Phone Number</th>
                            <th>Subject</th>
                            <th> selct</th>
                            </tr>
                        </thead>
                        <tbody>
                        { teachers.map((teacher) =>(
                             <tr>
                             <td>{teacher[0]}</td>
                             <td>{teacher[1]}</td>
                             <td>{teacher[2]}</td>
                             <td>{teacher[3]}</td>
                             <td>{teacher[4]}</td>
                             <td>
                                <div >
                                <Form.Check type='checkbox' id={'default-checkbox'} style={{backgroundColor:'red',padding:0}} />
                                </div>

                             </td>
                   
                             </tr>

                        ))
                        }
                        </tbody>

                </Table>

            </Row>

       
      
      </Container>
    );

}


export default ManageTeachers;
