import { useState } from 'react';
import { Modal , Button, Form} from 'react-bootstrap';
import axios from "axios"
import {Container , Row, Col,FloatingLabel,Dropdown, DropdownButton} from 'react-bootstrap'  
import './Login.css';
import { format } from 'path';




const Login = () => {

    const [signUp, setSignUp] = useState(false)
    const [submitResponce, setSubmitResponce] = useState('')
    const [submitStatus, setSubmitStatus] = useState('')
    const [show, setShow] = useState(false);

    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [fName, setFName] = useState("")
    const [LName, setLname] = useState("")
    const [phone, setPhoneNumber] = useState("")
    const [course, setCourse] = useState("Subject")

    const courses = ['Maths','Science','History', 'Geography', 'Add Maths', 'Biology','Computer Science']




    const checkLogin = () =>{

        var i = 0;
        if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(email)){
            i = 1;
        }
        
        if(password.length < 4){
            i = 1;
        }
        return i;
    }

    const checkSignup = () =>{

        var i = 0;
        if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(email)){
            i = 1;
        }
        
        if(password.length < 4){
            i = 1;
        }
        return i;
    }



    const handleLogin = () =>{

        if (checkLogin() === 1){

            setSubmitStatus("Failed!")
            setSubmitResponce("input format does not match")
            setShow(true)
        }
     

    }



    return(


        <Container className='signinContainer' style={{backgroundColor:'#FFD440', margin: '0 auto', marginTop : '15vh',padding:'30px',width:'30vw'}}>

            <Row style={{display:'inline-flex'}}>
                <Col>
                <Button variant="outline-secondary" onClick={() => {setSignUp(false)}}>Login</Button>
                </Col>
                <Col>
                <Button variant="outline-secondary" onClick={() => {setSignUp(true)}}>Signup</Button>
                </Col>
            </Row>

            <Row style={{paddingTop:'20px', paddingBottom:'20px'}}>
                {signUp ? <h1 style={{textAlign:'center'}}>Signup</h1> : <h1 style={{textAlign:'center'}}>Login</h1> }
            </Row>


            { signUp ? 
               
               <Row className="mb-5" style={{ marginLeft:'0'}}>
                        <Row className="mb-3" style={{ padding:'1px'}}>
                            <Col>
                                <FloatingLabel controlId="floatingInput" label="First Name" style={{paddingTop:'10px'}} >
                                    <Form.Control type="name" onChange={(e) => {setFName(e.target.value)}} placeholder="John" size="sm" style={{height:'40px'}}/>
                                </FloatingLabel>
                            </Col>
                            <Col>
                            <FloatingLabel controlId="floatingInput" label="Last Name" style={{paddingTop:'10px'}} >
                                    <Form.Control type="name" onChange={(e) => {setLname(e.target.value)}} placeholder="John" size="sm" style={{height:'40px'}}/>
                                </FloatingLabel>
                            </Col>
                        </Row>

                        <Row style={{ padding:'0px'}}>
                            <Col>
                            <FloatingLabel controlId="floatingInput" label="Phone Number" style={{paddingTop:'10px'}} >
                                    <Form.Control type="name" onChange={(e) => {setPhoneNumber(e.target.value)}} pattern="03[0-9]*.{9,9}" placeholder="Phone Number" maxLength={11} size="sm" style={{height:'40px'}}/>
                            </FloatingLabel>
                            </Col>


                            <Col style={{paddingTop: '11px'}} >

                                <DropdownButton id="dropdown-basic-button" title={course}>
                                {courses.map((cour) => (
        
                                    <Dropdown.Item href="#/action-1" onClick={() => {setCourse(cour)}}>{cour}</Dropdown.Item>

                                ))
                                }
                                </DropdownButton>
                                
                            </Col>
                        </Row>

               </Row>             
            : 
                
               <div>
               </div> 
                
            }



            <Row style={{ padding:'0px'}}>


                <FloatingLabel controlId="floatingInput" label="email" style={{padding:'7px'}} >
                        <Form.Control type="email" onChange={(e) => {setEmail(e.target.value)}} placeholder="example@gmail.com" size="sm" style={{height:'40px'}} required/>
                </FloatingLabel> 

            </Row>

            <Row>
                 <FloatingLabel controlId="floatingInput" label="password" style={{padding:'7px'}} >
                        <Form.Control type="password" onChange={(e) => {setPassword(e.target.value)}} placeholder="example@gmail.com" size="sm" style={{height:'40px'}} required/>
                </FloatingLabel>

            </Row>

            
            <Row md={5} className="justify-content-md-center pt-3">

                {signUp ? <Button variant="primary" size="sm">Signup</Button> : 
                
                <Button variant="primary" size="sm" onClick={handleLogin}>Login</Button> }

            </Row>

            <Modal show={show} onHide={() => {setShow(false)}} animation={true} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{submitStatus}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    {submitResponce}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="primary" onClick={() => {setShow(false)}}>
                        Okay
                    </Button>
                </Modal.Footer>
            </Modal>

        </Container>
    );

}



export default Login;
