import { Col , Row , Container,Form } from 'react-bootstrap';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Table from 'react-bootstrap/Table';
import { Link } from "react-router-dom";
import axios from "axios"
import './Navigation.css';
import { useEffect, useState } from 'react';





const ManageTeachers = () => {

    const [teachers, setTeachers] = useState([]);
    const [selected, setSelected] = useState([]);

    useEffect( async () =>{
        
        try {
            await axios.get("https://localhost:44323/api/GetTeachers",)
            .then((responce) => {
  
                setTeachers(responce.data)
        })
        } 
        catch (error) {
            console.error(error)
        }
    },[])

    const handleCheck = (id) => {

        if (selected.includes(id)){
            var arr = selected;
            arr = arr.splice(selected.indexOf(id), selected.indexOf(id));
            setSelected(arr)
        }
        else{
            var arr = selected;
            arr.push(id)
            setSelected(arr)
        }
        console.log(selected)
    }




    return(
        <Container style={{marginTop:'50px', maxWidth:'1000px',paddingLeft:'3vw',paddingRight:'3vw'}}>

            <Row>
                <h1>Manage Teachers</h1>
            </Row>
            <Row>
            <Button variant="danger">Remove</Button>{' '}
            </Row>

            <Row style={{marginTop:'50px'}}>

            <Table striped bordered hover variant="dark">
                        <thead>
                            <tr>
                            <th>ID</th>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Phone Number</th>
                            <th>Subject</th>
                            <th></th>
                            </tr>
                        </thead>
                        <tbody>
                        { teachers.map((teacher) =>(
                             <tr>
                             <td>{teacher[0]}</td>
                             <td>{teacher[1]}</td>
                             <td>{teacher[2]}</td>
                             <td>{teacher[3]}</td>
                             <td>{teacher[4]}</td>
                             <td>
                                <Form.Check type='checkbox' id={'default-checkbox'} style={{backgroundColor:'red',paddingLeft:'40px'}} onChange={() => {handleCheck(teacher[0])}} />
                             </td>
                   
                             </tr>

                        ))
                        }
                        </tbody>

                </Table>

            </Row>

       
      
      </Container>
    );

}


export default ManageTeachers;
